example.code:
gradle { name = "Test my project in Docker"
tasks = "clean test" buildFile = "build-test.gradle" incremental = true workingDir = "tests/" gradleParams = "%myproject.version%"
useGradleWrapper = false
enableStacktrace = true
coverageEngine = idea { includeClasses = """ org.group.example.* org.group.common """.trimIndent() excludeClasses = "org.group.common.test.*" }
dockerImage = "gradle:jdk11" dockerImagePlatform = GradleBuildStep.ImagePlatform.Linux }

gradle { name = "Default run on JDK 11" executionMode = BuildStep.ExecutionMode.RUN_ON_FAILURE
buildFile = "build-dev.gradle" gradleHome = "%env.GRADLE_DEV_HOME%" useGradleWrapper = false
jdkHome = "%env.JDK_11_0%" jvmArgs = "-Xmx2048m" }
